    puglatizer["pages"]["archive"]["privacy_LEGACY"] = function template(a){var e,s,r="";try{s=1,e="/Users/alyshahudson/smart-tracing/src/templates/pages/archive/privacy_LEGACY.pug",r+='<div class="content">',s=2,e="/Users/alyshahudson/smart-tracing/src/templates/pages/archive/privacy_LEGACY.pug",r+='<div class="container" style="padding:0;">',s=3,e="/Users/alyshahudson/smart-tracing/src/templates/pages/archive/privacy_LEGACY.pug",r+='<div class="card card-lg">',s=4,e="/Users/alyshahudson/smart-tracing/src/templates/pages/archive/privacy_LEGACY.pug",r+='<div class="card-body">',s=5,e="/Users/alyshahudson/smart-tracing/src/templates/pages/archive/privacy_LEGACY.pug",r+='<div class="markdown">',s=6,e="/Users/alyshahudson/smart-tracing/src/templates/pages/archive/privacy_LEGACY.pug",r+='<div class="d-flex"></div>',s=7,e="/Users/alyshahudson/smart-tracing/src/templates/pages/archive/privacy_LEGACY.pug",r+="<h1>",s=7,e="/Users/alyshahudson/smart-tracing/src/templates/pages/archive/privacy_LEGACY.pug",r+="Privacy Policy</h1>",s=9,e="/Users/alyshahudson/smart-tracing/src/templates/pages/archive/privacy_LEGACY.pug",r+="<i>",s=9,e="/Users/alyshahudson/smart-tracing/src/templates/pages/archive/privacy_LEGACY.pug",r+="Last updated: 28 March 2020</i>",s=10,e="/Users/alyshahudson/smart-tracing/src/templates/pages/archive/privacy_LEGACY.pug",r+="<p></p>",s=11,e="/Users/alyshahudson/smart-tracing/src/templates/pages/archive/privacy_LEGACY.pug",r+="<p>",s=11,e="/Users/alyshahudson/smart-tracing/src/templates/pages/archive/privacy_LEGACY.pug",r+="Zerobase Foundation (“Zerobase”) commits completely to protecting your privacy. This privacy policy was created in order to demonstrate Zerobase’s unfaltering prioritization of your privacy as a user of the Zerobase platform (the “Platform). This policy explains what information is collected and how this information is used. As our technology evolves and laws change, we may update this notice and any supplemental privacy notices to reflect changes in our practices and applicable law. If we update this policy, we will post a notice of any material updates on our site.</p>",s=13,e="/Users/alyshahudson/smart-tracing/src/templates/pages/archive/privacy_LEGACY.pug",r+="<h4>",s=13,e="/Users/alyshahudson/smart-tracing/src/templates/pages/archive/privacy_LEGACY.pug",r+="Links to Third Party Sites</h4>",s=14,e="/Users/alyshahudson/smart-tracing/src/templates/pages/archive/privacy_LEGACY.pug",r+="<p>",s=14,e="/Users/alyshahudson/smart-tracing/src/templates/pages/archive/privacy_LEGACY.pug",r+="This notice does not apply to any third-party sites that may link to, or be accessible from, our sites. Your interactions with these sites are governed by the third parties’ applicable privacy notices, statements, or policies. We encourage you to read them. </p>",s=16,e="/Users/alyshahudson/smart-tracing/src/templates/pages/archive/privacy_LEGACY.pug",r+="<h2>",s=16,e="/Users/alyshahudson/smart-tracing/src/templates/pages/archive/privacy_LEGACY.pug",r+="What data we collect from you</h2>",s=18,e="/Users/alyshahudson/smart-tracing/src/templates/pages/archive/privacy_LEGACY.pug",r+="<h4>",s=18,e="/Users/alyshahudson/smart-tracing/src/templates/pages/archive/privacy_LEGACY.pug",r+="Data we collect automatically</h4>",s=19,e="/Users/alyshahudson/smart-tracing/src/templates/pages/archive/privacy_LEGACY.pug",r+="<p>",s=19,e="/Users/alyshahudson/smart-tracing/src/templates/pages/archive/privacy_LEGACY.pug",r+="We may collect what is called a device “fingerprint,” which tells us about the kind of phone you are using and its browser settings. Such data does not uniquely identify the device, meaning that many devices will have the same fingerprint.</p>",s=21,e="/Users/alyshahudson/smart-tracing/src/templates/pages/archive/privacy_LEGACY.pug",r+="<h4>",s=21,e="/Users/alyshahudson/smart-tracing/src/templates/pages/archive/privacy_LEGACY.pug",r+="Data you provide about yourself</h4>",s=22,e="/Users/alyshahudson/smart-tracing/src/templates/pages/archive/privacy_LEGACY.pug",r+="<p>",s=22,e="/Users/alyshahudson/smart-tracing/src/templates/pages/archive/privacy_LEGACY.pug",r+="You may voluntarily provide your email or phone number for the sole purpose of receiving notifications. If you contact us via email, you may give us your email address and any personal data contained in any comments, photos, feedback, or other information you submit. We will not use this information for any purpose except to respond to inquiries.</p>",s=24,e="/Users/alyshahudson/smart-tracing/src/templates/pages/archive/privacy_LEGACY.pug",r+="<h4>",s=24,e="/Users/alyshahudson/smart-tracing/src/templates/pages/archive/privacy_LEGACY.pug",r+="Data concerning health</h4>",s=25,e="/Users/alyshahudson/smart-tracing/src/templates/pages/archive/privacy_LEGACY.pug",r+="<p>",s=25,e="/Users/alyshahudson/smart-tracing/src/templates/pages/archive/privacy_LEGACY.pug",r+="We may collect what is considered data concerning health under the GDPR when you check in to a certain kind of provider of public health services. </p>",s=27,e="/Users/alyshahudson/smart-tracing/src/templates/pages/archive/privacy_LEGACY.pug",r+="<h4>",s=27,e="/Users/alyshahudson/smart-tracing/src/templates/pages/archive/privacy_LEGACY.pug",r+="Other special categories of data</h4>",s=28,e="/Users/alyshahudson/smart-tracing/src/templates/pages/archive/privacy_LEGACY.pug",r+="<p>",s=28,e="/Users/alyshahudson/smart-tracing/src/templates/pages/archive/privacy_LEGACY.pug",r+="We will not intentionally or unintentionally collect other “special categories of data” under the EU General Data Protection Regulation (“GDPR”) without your explicit consent for one or more specified purposes or as otherwise permitted or required by applicable law. Special categories of data include personal data (a) revealing racial or ethnic origin, political opinions, religious or philosophical beliefs, or trade union membership; or (b) concerning a natural person’s sex life or sexual orientation.</p>",s=30,e="/Users/alyshahudson/smart-tracing/src/templates/pages/archive/privacy_LEGACY.pug",r+="<h4>",s=30,e="/Users/alyshahudson/smart-tracing/src/templates/pages/archive/privacy_LEGACY.pug",r+="Minors</h4>",s=31,e="/Users/alyshahudson/smart-tracing/src/templates/pages/archive/privacy_LEGACY.pug",r+="<p>",s=31,e="/Users/alyshahudson/smart-tracing/src/templates/pages/archive/privacy_LEGACY.pug",r+="Our site is not intended for minors (individuals under the age of 13, or equivalent minimum age depending on jurisdiction), and we do not knowingly have users that are minors. </p>",s=33,e="/Users/alyshahudson/smart-tracing/src/templates/pages/archive/privacy_LEGACY.pug",r+="<h2>",s=33,e="/Users/alyshahudson/smart-tracing/src/templates/pages/archive/privacy_LEGACY.pug",r+="How we use your data</h2>",s=35,e="/Users/alyshahudson/smart-tracing/src/templates/pages/archive/privacy_LEGACY.pug",r+="<h4>",s=35,e="/Users/alyshahudson/smart-tracing/src/templates/pages/archive/privacy_LEGACY.pug",r+="Purposes</h4>",s=36,e="/Users/alyshahudson/smart-tracing/src/templates/pages/archive/privacy_LEGACY.pug",r+="<p>",s=36,e="/Users/alyshahudson/smart-tracing/src/templates/pages/archive/privacy_LEGACY.pug",r+="The data is being collected for purposes of disease control, epidemic response, and public health research. We may use your personal data to: send you information you have expressly chosen to receive [with your consent], contribute to our archive of information in the public interest [for our legitimate interests]; and/or comply with applicable law, court order, subpoena, or legal process served on us [to comply with legal obligations]. We may use data concerning health because processing is necessary for the purposes of: preventive or occupational medicine; the provision of health or social care or treatment; or the management of health or social care systems and services. We may use your IP address to find out your broad region, such as country or state, to determine whether we need to comply with GDPR or other privacy laws. </p>",s=38,e="/Users/alyshahudson/smart-tracing/src/templates/pages/archive/privacy_LEGACY.pug",r+="<h4>",s=38,e="/Users/alyshahudson/smart-tracing/src/templates/pages/archive/privacy_LEGACY.pug",r+="Legal bases under the GDPR</h4>",s=39,e="/Users/alyshahudson/smart-tracing/src/templates/pages/archive/privacy_LEGACY.pug",r+="<p>",s=39,e="/Users/alyshahudson/smart-tracing/src/templates/pages/archive/privacy_LEGACY.pug",r+="If you are in the European Union, we will collect and use your personal data only if we have one or more legal bases for doing so under the GDPR. This means we collect and use your personal data only where: you have given your consent for one or more specific purposes; it is necessary to perform a contract we are about to enter into or have entered into with you; it is necessary for our legitimate interests (or those of a third party) and your interests and fundamental rights do not override those interests; it is necessary to protect the vital interests of you or another natural person; or it is necessary to comply with a legal obligation. We will indicate in brackets the legal basis or bases on which we are relying following each purpose. Where we are relying on consent as the legal basis, we will notify you and seek additional consent before using your personal data for a new purpose that is inconsistent with the original purpose for which we collected it. (However, due to the purposes of Zerobase, this is unlikely.) </p>",s=41,e="/Users/alyshahudson/smart-tracing/src/templates/pages/archive/privacy_LEGACY.pug",r+="<h2>",s=41,e="/Users/alyshahudson/smart-tracing/src/templates/pages/archive/privacy_LEGACY.pug",r+="When we share your data</h2>",s=43,e="/Users/alyshahudson/smart-tracing/src/templates/pages/archive/privacy_LEGACY.pug",r+="<h4>",s=43,e="/Users/alyshahudson/smart-tracing/src/templates/pages/archive/privacy_LEGACY.pug",r+="Relevant authorities in the interest of public health</h4>",s=44,e="/Users/alyshahudson/smart-tracing/src/templates/pages/archive/privacy_LEGACY.pug",r+="<p>",s=44,e="/Users/alyshahudson/smart-tracing/src/templates/pages/archive/privacy_LEGACY.pug",r+="If you voluntarily provided your email or phone number, we may provide your email or phone number to relevant local, state, or federal authorities, such as healthcare providers, in the interest of public health, for example contacting you to inform you that you may have been exposed to an infectious disease. </p>",s=46,e="/Users/alyshahudson/smart-tracing/src/templates/pages/archive/privacy_LEGACY.pug",r+="<h4>",s=46,e="/Users/alyshahudson/smart-tracing/src/templates/pages/archive/privacy_LEGACY.pug",r+="Law enforcement</h4>",s=47,e="/Users/alyshahudson/smart-tracing/src/templates/pages/archive/privacy_LEGACY.pug",r+="<p>",s=47,e="/Users/alyshahudson/smart-tracing/src/templates/pages/archive/privacy_LEGACY.pug",r+="We may share your data with law enforcement, other government agencies or authorities, or third parties as required by applicable law or legal process served on us.</p>",s=49,e="/Users/alyshahudson/smart-tracing/src/templates/pages/archive/privacy_LEGACY.pug",r+="<h2>",s=49,e="/Users/alyshahudson/smart-tracing/src/templates/pages/archive/privacy_LEGACY.pug",r+="How we store and protect your data</h2>",s=51,e="/Users/alyshahudson/smart-tracing/src/templates/pages/archive/privacy_LEGACY.pug",r+="<h4>",s=51,e="/Users/alyshahudson/smart-tracing/src/templates/pages/archive/privacy_LEGACY.pug",r+="Storage and transfers </h4>",s=52,e="/Users/alyshahudson/smart-tracing/src/templates/pages/archive/privacy_LEGACY.pug",r+="<p>",s=52,e="/Users/alyshahudson/smart-tracing/src/templates/pages/archive/privacy_LEGACY.pug",r+="By default, we only store the anonymous ID that is generated the first time you use the Platform. We store your email or phone number if you choose to provide one of them for purposes of notification. We do not store IP addresses. We never store personal health information. </p>",s=54,e="/Users/alyshahudson/smart-tracing/src/templates/pages/archive/privacy_LEGACY.pug",r+="<h4>",s=54,e="/Users/alyshahudson/smart-tracing/src/templates/pages/archive/privacy_LEGACY.pug",r+="Storage period</h4>",s=55,e="/Users/alyshahudson/smart-tracing/src/templates/pages/archive/privacy_LEGACY.pug",r+="<p>",s=55,e="/Users/alyshahudson/smart-tracing/src/templates/pages/archive/privacy_LEGACY.pug",r+="We will store data until it is no longer needed to fulfill the purpose(s) for which it was collected or as otherwise required or permitted by law. At such time, this data will be deleted. If this is not possible, we will securely store your personal data and isolate it from any further use until deletion is possible. We may dispose of any data at our discretion without notice, subject to applicable law. </p>",s=57,e="/Users/alyshahudson/smart-tracing/src/templates/pages/archive/privacy_LEGACY.pug",r+="<h4>",s=57,e="/Users/alyshahudson/smart-tracing/src/templates/pages/archive/privacy_LEGACY.pug",r+="Protection</h4>",s=58,e="/Users/alyshahudson/smart-tracing/src/templates/pages/archive/privacy_LEGACY.pug",r+="<p>",s=58,e="/Users/alyshahudson/smart-tracing/src/templates/pages/archive/privacy_LEGACY.pug",r+="As the transmission of data via the internet is not completely secure, we cannot guarantee the security of your information transmitted to our sites and any such transmission is at your own risk. However, we maintain appropriate technical and organizational measures to prevent unauthorized disclosure of, or access to, data.</p>",s=60,e="/Users/alyshahudson/smart-tracing/src/templates/pages/archive/privacy_LEGACY.pug",r+="<h4>",s=60,e="/Users/alyshahudson/smart-tracing/src/templates/pages/archive/privacy_LEGACY.pug",r+="Deletion</h4>",s=61,e="/Users/alyshahudson/smart-tracing/src/templates/pages/archive/privacy_LEGACY.pug",r+="<p>",s=61,e="/Users/alyshahudson/smart-tracing/src/templates/pages/archive/privacy_LEGACY.pug",r+="If you provided your email or phone number and would like it to be deleted, simply contact us and we will no longer store them.  </p>",s=63,e="/Users/alyshahudson/smart-tracing/src/templates/pages/archive/privacy_LEGACY.pug",r+="<h4>",s=63,e="/Users/alyshahudson/smart-tracing/src/templates/pages/archive/privacy_LEGACY.pug",r+="How to contact us</h4>",s=64,e="/Users/alyshahudson/smart-tracing/src/templates/pages/archive/privacy_LEGACY.pug",r+="<p>",s=64,e="/Users/alyshahudson/smart-tracing/src/templates/pages/archive/privacy_LEGACY.pug",r+=" ",s=65,e="/Users/alyshahudson/smart-tracing/src/templates/pages/archive/privacy_LEGACY.pug",r+="You can contact us at info@zerobase.io or our Data Protection Officer (“DPO”) at ",s=66,e="/Users/alyshahudson/smart-tracing/src/templates/pages/archive/privacy_LEGACY.pug",r+='<a href="mailto:david@zerobase.io">',s=66,e="/Users/alyshahudson/smart-tracing/src/templates/pages/archive/privacy_LEGACY.pug",r+="david@zerobase.io. </a></p></div></div></div></div></div>"}catch(t){pug.rethrow(t,e,s)}return r};

