    puglatizer["pages"]["healthcare"] = function template(a){var e,s,t="";try{s=1,e="/Users/alyshahudson/smart-tracing/src/templates/pages/healthcare.pug",t+='<div class="content">',s=2,e="/Users/alyshahudson/smart-tracing/src/templates/pages/healthcare.pug",t+='<div class="container" style="padding:0;">',s=3,e="/Users/alyshahudson/smart-tracing/src/templates/pages/healthcare.pug",t+='<div class="card card-lg">',s=4,e="/Users/alyshahudson/smart-tracing/src/templates/pages/healthcare.pug",t+='<div class="card-body">',s=5,e="/Users/alyshahudson/smart-tracing/src/templates/pages/healthcare.pug",t+='<div class="markdown">',s=6,e="/Users/alyshahudson/smart-tracing/src/templates/pages/healthcare.pug",t+='<div class="d-flex"></div>',s=7,e="/Users/alyshahudson/smart-tracing/src/templates/pages/healthcare.pug",t+="<h2>",s=7,e="/Users/alyshahudson/smart-tracing/src/templates/pages/healthcare.pug",t+="Q&A about Zerobase for Healthcare Providers</h2>",s=9,e="/Users/alyshahudson/smart-tracing/src/templates/pages/healthcare.pug",t+='<a class="btn btn-primary" href="#" id="register-healthcare">',s=10,e="/Users/alyshahudson/smart-tracing/src/templates/pages/healthcare.pug",t+='<i class="fe fe-check-square mr-2"></i>',s=11,e="/Users/alyshahudson/smart-tracing/src/templates/pages/healthcare.pug",t+="Generate and Print QR</a>",s=13,e="/Users/alyshahudson/smart-tracing/src/templates/pages/healthcare.pug",t+="<h2>",s=13,e="/Users/alyshahudson/smart-tracing/src/templates/pages/healthcare.pug",t+="What is Zerobase? </h2>",s=14,e="/Users/alyshahudson/smart-tracing/src/templates/pages/healthcare.pug",t+="<p>",s=14,e="/Users/alyshahudson/smart-tracing/src/templates/pages/healthcare.pug",t+="Zerobase ​is a tool used by state and local health officials to pinpoint individuals likeliest to next develop COVID-19. Zerobase uses anonymized predictive technology to help protect communities before an outbreak occurs, enabling officials to best allocate their resources, eliminating guesswork, and reducing illness and fear.</p>",s=16,e="/Users/alyshahudson/smart-tracing/src/templates/pages/healthcare.pug",t+="<h2>",s=16,e="/Users/alyshahudson/smart-tracing/src/templates/pages/healthcare.pug",t+="How much does it cost? </h2>",s=17,e="/Users/alyshahudson/smart-tracing/src/templates/pages/healthcare.pug",t+="<p>",s=17,e="/Users/alyshahudson/smart-tracing/src/templates/pages/healthcare.pug",t+="Zerobase is being offered without charge. It has been organized as a nonprofit effort and labor has been donated.</p>",s=19,e="/Users/alyshahudson/smart-tracing/src/templates/pages/healthcare.pug",t+="<h2>",s=19,e="/Users/alyshahudson/smart-tracing/src/templates/pages/healthcare.pug",t+="Who is building Zerobase? </h2>",s=20,e="/Users/alyshahudson/smart-tracing/src/templates/pages/healthcare.pug",t+="<p>",s=20,e="/Users/alyshahudson/smart-tracing/src/templates/pages/healthcare.pug",t+="Zerobase was created by a team of volunteer technologists and medical professionals concerned about the spread of coronavirus among their fellow citizens. Using local data, their program traces the likely path of travel of the virus, identifying the next prospective patients before illness can spread further.</p>",s=22,e="/Users/alyshahudson/smart-tracing/src/templates/pages/healthcare.pug",t+="<h2>",s=22,e="/Users/alyshahudson/smart-tracing/src/templates/pages/healthcare.pug",t+="How does it work? </h2>",s=23,e="/Users/alyshahudson/smart-tracing/src/templates/pages/healthcare.pug",t+="<p>",s=23,e="/Users/alyshahudson/smart-tracing/src/templates/pages/healthcare.pug",t+="Local governments work with businesses, places of worship, and other gathering spaces in their communities to post a QR code at the entrance or another obvious highly trafficked spot. Individuals visiting those locations scan the QR code on their way in and their information is then anonymized and analyzed to ascertain likely patterns of spread in that community. The data builds up invisible networks of interaction among people. If someone tests positive, the program can inform local health authorities of the next likely people to contract and spread illness. Patients and prospective patients are informed anonymously via their cell phones. The more data that is collected and aggregated, the more accurate its predictive properties. Over time, communities deploying Zerobase help sister communities to be even better prepared for viral spread.</p>",s=25,e="/Users/alyshahudson/smart-tracing/src/templates/pages/healthcare.pug",t+="<h2>",s=25,e="/Users/alyshahudson/smart-tracing/src/templates/pages/healthcare.pug",t+="What information is collected by Zerobase? </h2>",s=26,e="/Users/alyshahudson/smart-tracing/src/templates/pages/healthcare.pug",t+="<p>",s=26,e="/Users/alyshahudson/smart-tracing/src/templates/pages/healthcare.pug",t+="An individual’s identity is never known by the system. Personally identifying information is therefore not stored and not subject to discovery by any other party.</p>",s=28,e="/Users/alyshahudson/smart-tracing/src/templates/pages/healthcare.pug",t+="<h2>",s=28,e="/Users/alyshahudson/smart-tracing/src/templates/pages/healthcare.pug",t+="What technological resources does Zerobase require? </h2>",s=29,e="/Users/alyshahudson/smart-tracing/src/templates/pages/healthcare.pug",t+="<p>",s=29,e="/Users/alyshahudson/smart-tracing/src/templates/pages/healthcare.pug",t+="A computer and a printer is all that is required for local public health efforts to activate Zerobase. Individual community members only need a cell phone.</p>",s=32,e="/Users/alyshahudson/smart-tracing/src/templates/pages/healthcare.pug",t+="<h2>",s=32,e="/Users/alyshahudson/smart-tracing/src/templates/pages/healthcare.pug",t+="How do I contact Zerobase?</h2>",s=33,e="/Users/alyshahudson/smart-tracing/src/templates/pages/healthcare.pug",t+="<p>",s=33,e="/Users/alyshahudson/smart-tracing/src/templates/pages/healthcare.pug",t+=" ",s=34,e="/Users/alyshahudson/smart-tracing/src/templates/pages/healthcare.pug",t+="You can always contact us at ",s=35,e="/Users/alyshahudson/smart-tracing/src/templates/pages/healthcare.pug",t+='<a href="mailto:info@zerobase.io">',s=35,e="/Users/alyshahudson/smart-tracing/src/templates/pages/healthcare.pug",t+="info@zerobase.io</a>",s=36,e="/Users/alyshahudson/smart-tracing/src/templates/pages/healthcare.pug",t+=".</p></div></div></div></div></div>"}catch(r){pug.rethrow(r,e,s)}return t};

